// frontend/src/pages/CustomerListPage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import { Table, Button, Card, Alert, Form, InputGroup, Row, Col, ButtonToolbar } from 'react-bootstrap';
import { Plus, Upload, Wrench } from 'react-bootstrap-icons';

function CustomerListPage() {
    const [customers, setCustomers] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchCustomers();
    }, []);

    const fetchCustomers = async () => {
        // ... (fetch logic is fine) ...
    };
    
    const formatCurrency = (amount, currency) => {
        return new Intl.NumberFormat('en-US', { style: 'currency', currency: currency }).format(amount);
    };

    return (
        <>
            {error && <Alert variant="danger">{error}</Alert>}
            
            <Row className="mb-3">
                <Col>
                    <ButtonToolbar>
                        <Button variant="success" className="me-2" onClick={() => navigate('/customers/new')}>
                            <Plus size={20} /> Add New Customer
                        </Button>
                        <Button variant="warning" className="me-2">
                            <Upload size={20} /> Upload from Excel
                        </Button>
                         <Button variant="info">
                            <Wrench size={20} /> Bulk Update Customers
                        </Button>
                    </ButtonToolbar>
                </Col>
                <Col>
                    <InputGroup>
                        <Form.Control placeholder="Search... (min. 3 chars)" />
                        <Button variant="outline-secondary">Search</Button>
                    </InputGroup>
                </Col>
            </Row>

            <Card>
                <Card.Header><h4>Customers</h4></Card.Header>
                <Card.Body>
                    <Table striped bordered hover responsive>
                        <thead className="table-dark">
                            <tr>
                                <th>Name / Title</th>
                                <th>Open Balance</th>
                                <th>Check/Note Balance</th>
                            </tr>
                        </thead>
                        <tbody>
                            {customers.map((customer) => (
                                <tr key={customer.id} onClick={() => navigate(`/customers/edit/${customer.id}`)} style={{cursor: 'pointer'}}>
                                    <td>{customer.name}</td>
                                    {/* --- Display formatted currency --- */}
                                    <td>{formatCurrency(customer.open_balance, customer.currency)}</td>
                                    <td>{formatCurrency(0, customer.currency)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </Card.Body>
            </Card>
        </>
    );
}

export default CustomerListPage;
