// frontend/src/pages/CustomerListPage.js

import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import { Table, Button, Card, Alert, Form, InputGroup, Row, Col, ButtonToolbar } from 'react-bootstrap';
import { Plus, Upload, Wrench } from 'react-bootstrap-icons';

function CustomerListPage() {
    const [customers, setCustomers] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchCustomers();
    }, []);

    const fetchCustomers = async () => {
        try {
            const response = await axiosInstance.get('/customers/');
            setCustomers(response.data);
        } catch (error) {
            setError('Failed to fetch customers.');
            console.error('Failed to fetch customers:', error);
        }
    };
    
    // We will add delete functionality back on the edit page
    
    return (
        <>
            {error && <Alert variant="danger">{error}</Alert>}
            
            <Row className="mb-3">
                <Col>
                    <ButtonToolbar>
                        <Button variant="success" className="me-2" onClick={() => navigate('/customers/new')}>
                            <Plus size={20} /> Yeni Müşteri Ekle (Add New)
                        </Button>
                        <Button variant="warning" className="me-2">
                            <Upload size={20} /> Excel'den Müşteri Yükle (Upload)
                        </Button>
                         <Button variant="info">
                            <Wrench size={20} /> Toplu Müşteri Güncelleme (Bulk Update)
                        </Button>
                    </ButtonToolbar>
                </Col>
                <Col>
                    <InputGroup>
                        <Form.Control placeholder="Arama... (en az 3 karakter)" />
                        <Button variant="outline-secondary">Ara (Search)</Button>
                    </InputGroup>
                </Col>
            </Row>

            <Card>
                <Card.Header>
                    <h4>Müşteriler (Customers)</h4>
                </Card.Header>
                <Card.Body>
                    <Table striped bordered hover responsive>
                        <thead className="table-dark">
                            <tr>
                                <th>İsim / Unvan (Name / Title)</th>
                                <th>Açık Bakiye (Open Balance)</th>
                                <th>Çek/Senet Bakiyesi (Check/Note Balance)</th>
                            </tr>
                        </thead>
                        <tbody>
                            {customers.map((customer) => (
                                <tr key={customer.id} onClick={() => navigate(`/customers/edit/${customer.id}`)} style={{cursor: 'pointer'}}>
                                    <td>{customer.name}</td>
                                    <td>{parseFloat(customer.open_balance).toFixed(2)} KZT</td>
                                    <td>0,00 KZT</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </Card.Body>
            </Card>
        </>
    );
}

export default CustomerListPage;
