// frontend/src/pages/SaleDetailPage.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import { Card, Button, Spinner, Alert, Row, Col, Table } from 'react-bootstrap';

function SaleDetailPage() {
    const { id } = useParams(); // Gets the 'id' from the URL
    const navigate = useNavigate();
    const [sale, setSale] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchSaleDetails = async () => {
            try {
                setLoading(true);
                const response = await axiosInstance.get(`/sales/${id}/`);
                setSale(response.data);
                setError('');
            } catch (error) {
                console.error('Failed to fetch sale details:', error);
                setError('Could not load sale details. It may not exist.');
            } finally {
                setLoading(false);
            }
        };

        fetchSaleDetails();
    }, [id]); // Re-run if the id in the URL changes

    if (loading) {
        return <div className="text-center"><Spinner animation="border" /></div>;
    }

    if (error) {
        return <Alert variant="danger">{error}</Alert>;
    }

    return (
        <div>
            <Button variant="secondary" onClick={() => navigate('/sales')} className="mb-3">
                &larr; Back to Sales List
            </Button>
            {sale && (
                <Card>
                    <Card.Header>
                        <h4>Sale Invoice #{sale.invoice_number || sale.id}</h4>
                    </Card.Header>
                    <Card.Body>
                        <Row className="mb-4">
                            <Col md={6}>
                                <h5>Customer Details:</h5>
                                <p>
                                    <strong>Name:</strong> {sale.customer_name} <br />
                                    <strong>Customer ID:</strong> {sale.customer}
                                </p>
                            </Col>
                            <Col md={6} className="text-md-end">
                                <h5>Sale Information:</h5>
                                <p>
                                    <strong>Sale Date:</strong> {new Date(sale.sale_date).toLocaleDateString()}
                                </p>
                            </Col>
                        </Row>

                        <h5>Items Sold</h5>
                        <Table striped bordered hover responsive>
                            <thead>
                                <tr>
                                    <th>#</th>
                                    <th>Product</th>
                                    <th>Quantity</th>
                                    <th>Unit Price</th>
                                    <th>Line Total</th>
                                </tr>
                            </thead>
                            <tbody>
                                {sale.items.map((item, index) => (
                                    <tr key={item.id}>
                                        <td>{index + 1}</td>
                                        <td>{item.product_name}</td>
                                        <td>{item.quantity}</td>
                                        <td>${parseFloat(item.unit_price).toFixed(2)}</td>
                                        <td>${(item.quantity * item.unit_price).toFixed(2)}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>

                        <Row className="mt-4">
                            <Col className="text-end">
                                <h3>Total Amount: ${parseFloat(sale.total_amount).toFixed(2)}</h3>
                            </Col>
                        </Row>
                    </Card.Body>
                    <Card.Footer className="text-end">
                        <Button variant="primary" className="me-2">Print Invoice</Button>
                        <Button variant="danger">Delete Sale</Button>
                    </Card.Footer>
                </Card>
            )}
        </div>
    );
}

export default SaleDetailPage;
