// frontend/src/pages/EditPurchasePage.js

import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';
import { useNavigate, useParams } from 'react-router-dom';
import { Form, Button, Card, Row, Col, Table, Alert, Spinner } from 'react-bootstrap';
import { FaTrash } from 'react-icons/fa';

function EditPurchasePage() {
    const { id } = useParams();
    const navigate = useNavigate();

    // Data state
    const [suppliers, setSuppliers] = useState([]);
    const [products, setProducts] = useState([]);

    // Form state
    const [formData, setFormData] = useState({
        supplier_id: '',
        purchase_date: '',
        bill_number: '',
        items: []
    });

    // UI state
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchInitialData = async () => {
            try {
                const [suppliersRes, productsRes, purchaseRes] = await Promise.all([
                    axiosInstance.get('/suppliers/'),
                    axiosInstance.get('/products/'),
                    axiosInstance.get(`/purchases/${id}/`)
                ]);

                setSuppliers(suppliersRes.data);
                setProducts(productsRes.data);

                // Pre-fill the form with existing purchase data
                const purchaseData = purchaseRes.data;
                setFormData({
                    supplier_id: purchaseData.supplier,
                    purchase_date: purchaseData.purchase_date,
                    bill_number: purchaseData.bill_number || '',
                    items: purchaseData.items.map(item => ({
                        product_id: item.product.id,
                        quantity: item.quantity,
                        unit_price: item.unit_price
                    }))
                });

            } catch (err) {
                setError('Failed to load initial data.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };
        fetchInitialData();
    }, [id]);

    const handleInputChange = (e) => { /* ... (same as PurchaseListPage) ... */ };
    const handleItemChange = (index, event) => { /* ... (same as PurchaseListPage) ... */ };
    const handleAddItem = () => { /* ... (same as PurchaseListPage) ... */ };
    const handleRemoveItem = (index) => { /* ... (same as PurchaseListPage) ... */ };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Use the PUT method for updates
            await axiosInstance.put(`/purchases/${id}/`, formData);
            navigate(`/purchases/${id}`); // Go back to the detail page
        } catch (err) {
            console.error('Failed to update purchase:', err.response?.data);
            setError('Failed to update purchase. Please check all fields.');
        }
    };
    
    // Copy-paste helper functions here from PurchaseListPage.js
    // ...

    if (loading) return <div className="text-center"><Spinner animation="border" /></div>;
    if (error && !formData.items.length) return <Alert variant="danger">{error}</Alert>;

    return (
        <Card>
            <Card.Header><h4>Edit Purchase</h4></Card.Header>
            <Card.Body>
                {/* ... (The entire JSX form from the Modal in PurchaseListPage.js goes here) ... */}
            </Card.Body>
        </Card>
    );
}

// NOTE: You need to copy the helper functions and the JSX for the form from PurchaseListPage.js
// into the EditPurchasePage.js file where indicated.
export default EditPurchasePage;
