// frontend/src/pages/CustomerListPage.js

import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';
import { Table, Button, Card, Alert } from 'react-bootstrap';
import CustomerFormModal from '../components/CustomerFormModal'; // Import the modal

function CustomerListPage() {
    const [customers, setCustomers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [editingCustomer, setEditingCustomer] = useState(null);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        fetchCustomers();
    }, []);

    const fetchCustomers = async () => {
        try {
            const response = await axiosInstance.get('/customers/');
            setCustomers(response.data);
        } catch (error) {
            setError('Failed to fetch customers.');
            console.error('Failed to fetch customers:', error);
        }
    };

    const handleShowModal = (customer = null) => {
        setEditingCustomer(customer);
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setEditingCustomer(null);
    };

    const handleSaveCustomer = async (customerData) => {
        try {
            if (editingCustomer) {
                // Update existing customer
                await axiosInstance.put(`/customers/${editingCustomer.id}/`, customerData);
                setSuccess('Customer updated successfully!');
            } else {
                // Create new customer
                await axiosInstance.post('/customers/', customerData);
                setSuccess('Customer created successfully!');
            }
            fetchCustomers(); // Refresh the list
            handleCloseModal();
        } catch (error) {
            setError('Failed to save customer.');
            console.error('Failed to save customer:', error);
        }
    };

    const handleDeleteCustomer = async (customerId) => {
        // Add a confirmation dialog before deleting
        if (window.confirm('Are you sure you want to delete this customer?')) {
            try {
                await axiosInstance.delete(`/customers/${customerId}/`);
                setSuccess('Customer deleted successfully!');
                fetchCustomers(); // Refresh the list
            } catch (error) {
                setError('Failed to delete customer.');
                console.error('Failed to delete customer:', error);
            }
        }
    };

    return (
        <>
            {success && <Alert variant="success" onClose={() => setSuccess('')} dismissible>{success}</Alert>}
            {error && <Alert variant="danger" onClose={() => setError('')} dismissible>{error}</Alert>}
            
            <Card>
                <Card.Header className="d-flex justify-content-between align-items-center">
                    <h4>Customers</h4>
                    <Button variant="primary" onClick={() => handleShowModal()}>
                        + New Customer
                    </Button>
                </Card.Header>
                <Card.Body>
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {customers.map((customer, index) => (
                                <tr key={customer.id}>
                                    <td>{index + 1}</td>
                                    <td>{customer.name}</td>
                                    <td>{customer.email || 'N/A'}</td>
                                    <td>{customer.phone || 'N/A'}</td>
                                    <td>
                                        <Button variant="info" size="sm" className="me-2" onClick={() => handleShowModal(customer)}>Edit</Button>
                                        <Button variant="danger" size="sm" onClick={() => handleDeleteCustomer(customer.id)}>Delete</Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </Card.Body>
            </Card>

            <CustomerFormModal
                show={showModal}
                handleClose={handleCloseModal}
                customer={editingCustomer}
                onSave={handleSaveCustomer}
            />
        </>
    );
}

export default CustomerListPage;
