// frontend/src/pages/CustomerFormPage.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import { Container, Card, Form, Button, Tabs, Tab, Row, Col, Alert } from 'react-bootstrap';
import { Image } from 'react-bootstrap-icons';

function CustomerFormPage() {
    const { id } = useParams(); // Get the ID from the URL if we are editing
    const navigate = useNavigate();
    const isEditing = Boolean(id);

    const [formData, setFormData] = useState({ name: '', email: '', phone: '', address: '' });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    useEffect(() => {
        if (isEditing) {
            const fetchCustomer = async () => {
                try {
                    const response = await axiosInstance.get(`/customers/${id}/`);
                    setFormData(response.data);
                } catch (error) {
                    setError('Failed to fetch customer details.');
                }
            };
            fetchCustomer();
        }
    }, [id, isEditing]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const apiCall = isEditing 
            ? axiosInstance.put(`/customers/${id}/`, formData)
            : axiosInstance.post('/customers/', formData);

        try {
            await apiCall;
            setSuccess(`Customer successfully ${isEditing ? 'updated' : 'created'}!`);
            setTimeout(() => navigate('/customers'), 1500); // Redirect after a short delay
        } catch (error) {
            setError('Failed to save customer.');
        }
    };

    return (
        <Container>
            <Form onSubmit={handleSubmit}>
                <Card>
                    <Card.Header className="d-flex justify-content-between">
                        <h4>{isEditing ? 'Edit Customer' : 'New Customer'}</h4>
                        <div>
                            <Button variant="success" type="submit" className="me-2">Kaydet (Save)</Button>
                            <Button variant="secondary" onClick={() => navigate('/customers')}>Geri Dön (Go Back)</Button>
                        </div>
                    </Card.Header>
                    <Card.Body>
                        {error && <Alert variant="danger">{error}</Alert>}
                        {success && <Alert variant="success">{success}</Alert>}
                        <Tabs defaultActiveKey="identity" id="customer-form-tabs" className="mb-3">
                            <Tab eventKey="identity" title="Kimlik Bilgileri (Identity)">
                                <Row>
                                    <Col md={8}>
                                        <Form.Group>
                                            <Form.Label>İsim / Unvan (Name / Title)</Form.Label>
                                            <Form.Control type="text" name="name" value={formData.name} onChange={handleChange} required />
                                        </Form.Group>
                                    </Col>
                                    <Col md={4}>
                                        <div className="d-flex flex-column align-items-center justify-content-center h-100 border rounded bg-light">
                                            <Image size={50} />
                                            <Button variant="link">Resim Ekle (Add Image)</Button>
                                        </div>
                                    </Col>
                                </Row>
                            </Tab>
                            <Tab eventKey="contact" title="İletişim (Contact)">
                                <Form.Group className="mb-3">
                                    <Form.Label>Email</Form.Label>
                                    <Form.Control type="email" name="email" value={formData.email} onChange={handleChange} />
                                </Form.Group>
                                <Form.Group className="mb-3">
                                    <Form.Label>Phone</Form.Label>
                                    <Form.Control type="text" name="phone" value={formData.phone} onChange={handleChange} />
                                </Form.Group>
                                 <Form.Group>
                                    <Form.Label>Address</Form.Label>
                                    <Form.Control as="textarea" rows={3} name="address" value={formData.address} onChange={handleChange} />
                                </Form.Group>
                            </Tab>
                             <Tab eventKey="financials" title="Cari (Financials)" disabled>
                                <p>Financial details will be shown here.</p>
                            </Tab>
                        </Tabs>
                    </Card.Body>
                </Card>
            </Form>
        </Container>
    );
}

export default CustomerFormPage;
