// frontend/src/pages/DashboardPage.js

import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';
import { Row, Col, Card, Spinner } from 'react-bootstrap';
import { PeopleFill, BoxArrowUpRight, CashStack } from 'react-bootstrap-icons';

function DashboardPage() {
    const [summary, setSummary] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                const response = await axiosInstance.get('/dashboard-summary/');
                setSummary(response.data);
            } catch (error) {
                console.error("Failed to fetch dashboard summary:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSummary();
    }, []);

    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(amount);
    };

    if (loading) {
        return <div className="d-flex justify-content-center align-items-center" style={{height: '80vh'}}><Spinner animation="border" /></div>;
    }

    return (
        <>
            <h1 className="mb-4">Dashboard</h1>
            
            <Row className="mb-4">
                <Col md={4}>
                    <Card bg="primary" text="white" className="h-100">
                        <Card.Body className="d-flex justify-content-between align-items-center">
                            <div>
                                <Card.Title>Total Receivables</Card.Title>
                                <Card.Text className="fs-2 fw-bold">{formatCurrency(summary.total_receivables)}</Card.Text>
                            </div>
                            <CashStack size={40} />
                        </Card.Body>
                    </Card>
                </Col>
                <Col md={4}>
                    <Card bg="danger" text="white" className="h-100">
                        <Card.Body className="d-flex justify-content-between align-items-center">
                            <div>
                                <Card.Title>Total Payables</Card.Title>
                                <Card.Text className="fs-2 fw-bold">{formatCurrency(summary.total_payables)}</Card.Text>
                            </div>
                            <BoxArrowUpRight size={40} />
                        </Card.Body>
                    </Card>
                </Col>
                <Col md={4}>
                    <Card bg="success" text="white" className="h-100">
                         <Card.Body className="d-flex justify-content-between align-items-center">
                            <div>
                                <Card.Title>Total Customers</Card.Title>
                                <Card.Text className="fs-2 fw-bold">{summary.customer_count}</Card.Text>
                            </div>
                            <PeopleFill size={40} />
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
            {/* You can add more rows here for other dynamic data later */}
        </>
    );
}

export default DashboardPage;
